import React, { useState } from 'react';

const AddDepense = (addDepenseHandler) => {

    const [amount, setAmount]=useState({
        valeur: 0,
        cause: "",
        acteur: ""
    });

const submitHandler = () =>{
    addDepenseHandler(amount);
    setAmount({valeur: 0, cause: "", acteur: "" })
}

    return (
        <div>
            <form onSubmit={submitHandler}>
                <input type="number" 
                        name="valeur" 
                        placeholder="valeur" 
                        value={amount.valeur}
                        onChange={(e) => setAmount({valeur : e.target.value}) } /><br />
                        <input type="text" 
                        name="cause" 
                        placeholder="cause" 
                        value={amount.cause}
                        onChange={(e) => setAmount({cause : e.target.value}) } /><br />
                        <input type="text" 
                        name="acteur" 
                        placeholder="acteur" 
                        value={amount.acteur}
                        onChange={(e) => setAmount({acteur : e.target.value}) } /><br />
                        <button>Submit</button>

            </form>
            <h1>the value is {amount.valeur}</h1>
        </div>
    )
}

export default AddDepense;
