import React, { Component } from 'react'

export default class AddDepense extends Component {
    state = {
        valeur: "",
        cause: "",
        acteur: "",
    }
    add = (e) => {
        e.preventDefault();
        if(this.state.valeur === "" && this.state.cause === "" && this.state.acteur === ""){
            alert("All the fields are mandatory");
            return;
        }
        this.props.addDepenseHandler(this.state);
        this.setState({amount: "", cause: "", acteur:""});
    }
    render() {
        return (          
        <div className="ui container">

            <form className="ui form" onSubmit={this.add}>
                <div className="field">
                    <label>valeur</label>
                    <input 
                        type="number" 
                        name="valeur"
                        value={this.state.valeur}
                        onChange={(e) => this.setState({valeur: e.target.value})}
                    />
                </div>
                <div className="field">
                    <label>Cause</label>
                    <input 
                        type="text" 
                        name="cause" 
                        placeholder="cause" 
                    />
                </div>
                <div className="field">
                    <label>Acteur</label>
                    <input 
                        type="text" 
                        name="acteur" 
                        placeholder="acteur" 
                    />
                </div>
                <button className="ui button" type="submit">Valider </button>               
            </form>
        </div>

        )
    }
}
