        return amount;
      }
    });
  }
  useEffect(() => {
    const retriveAmounts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    if(retriveAmounts) setAmounts(retriveAmounts)
  }, []);
