  localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(amounts))
}, [amounts])
  return (
    <div>
      <Header />
      <AddDepense addDepenseHandler={addDepenseHandler}/>
      <DepenseList 
        depenses={amounts}
        getAmountId = {removeAmountHandler}
        updateAmount = {updateAmount}
      />
    </div>
  );
}

export default App;
