import React from 'react'

const AddDepense = () => {
    state = {
        valeur: "",
        cause: "",
        acteur: "",
    }
    return (
        <div>
            <form>
                <input type="number" 
                        name="valeur" 
                        placeholder="valeur" 
                        value={this.state.valeur}
                        onChange={(e) => this.setState({cause: e.target.value})} />
                <input type="text" 
                        name="cause" 
                        placeholder="cause" 
                        value={this.state.cause}
                        onChange={(e) => this.setState({cause: e.target.value})} />
                <input type="text" 
                        name="acteur" 
                        placeholder="acteur" 
                        value={this.state.acteur}
                        onChange={(e) => this.setState({cause: e.target.value})} />          
            </form>
        </div>
    )
}

export default AddDepense;
